(function() {

    function getArtists() {
        $.ajax({
            url: `https://api.spotify.com/v1/me/top/artists?time_range=medium_term&limit=10`,
            headers: {
              Authorization: "Bearer " + access_token,
            },
            success: function (response) {
              var artistTrim = response.items.map((eachArtist, i) => ({
                name: eachArtist.name,
                popularity: eachArtist.popularity,
                genre: eachArtist.genres[0],
                index: i + 1
              }));

              let data = {
                artistList: artistTrim,
                json: true,
              };

              console.log(data.artistList)

              artistPlaceholder.innerHTML = artistTemplate({
                artists: data.artistList,
              });

            //   document
            //     .getElementById("download")
            //     .addEventListener("click", () => downloadImg(timeRangeSlug));
            },
          });
    }

        /**
         * Obtains parameters from the hash of the URL
         * @return Object
         */
        function getHashParams() {
          var hashParams = {};
          var e, r = /([^&;=]+)=?([^&;]*)/g,
              q = window.location.hash.substring(1);
          while ( e = r.exec(q)) {
             hashParams[e[1]] = decodeURIComponent(e[2]);
          }
          return hashParams;
        }

        var userProfileSource = document.getElementById('user-profile-template').innerHTML,
            userProfileTemplate = Handlebars.compile(userProfileSource),
            userProfilePlaceholder = document.getElementById('user-profile');

          var artistSource = document.getElementById('artist-template').innerHTML,
            artistTemplate = Handlebars.compile(artistSource),
            artistPlaceholder = document.getElementById('artist-profile');

        var oauthSource = document.getElementById('oauth-template').innerHTML,
            oauthTemplate = Handlebars.compile(oauthSource),
            oauthPlaceholder = document.getElementById('oauth');

        var params = getHashParams();

        var access_token = params.access_token,
            refresh_token = params.refresh_token,
            error = params.error;

        if (error) {
          alert('There was an error during the authentication');
        } else {
          if (access_token) {
            // render oauth info
            // oauthPlaceholder.innerHTML = oauthTemplate({
            //   access_token: access_token,
            //   refresh_token: refresh_token
            // });

            $.ajax({
                url: 'https://api.spotify.com/v1/me',
                headers: {
                  'Authorization': 'Bearer ' + access_token
                },
                success: function(response) {
                  console.log(response)
                  userProfilePlaceholder.innerHTML = userProfileTemplate(response);
                  $('#login').hide();
                  $('#loggedin').show();
                }
            });
          } else {
              // render initial screen
              $('#login').show();
              $('#loggedin').hide();
          }

          document.getElementById("show_artist").addEventListener(
            "click",
            function () {
                getArtists();
            },
            false
          );

          document.getElementById('obtain-new-token').addEventListener('click', function() {
            $.ajax({
              url: '/refresh_token',
              data: {
                'refresh_token': refresh_token
              }
            }).done(function(data) {
              access_token = data.access_token;
              oauthPlaceholder.innerHTML = oauthTemplate({
                access_token: access_token,
                refresh_token: refresh_token
              });
            });
          }, false);
        }
})();
